%rezolvare cerinte 3, 4, 5function f = solve()  a = 3;  b = 5;  lim_cerinta_1_2 = 10;  rect_sig(lim_cerinta_1_2,a,b);  pause(5);  trian_sig(lim_cerinta_1_2);  pause(5);    lim = 100;  t = 0:1:130;  t_u1 = 0:1:500;  t1 = 0:0.1:lim;  u1 = semnal_u1(lim);  u2 = semnal_u2(lim);  u3 = semnal_u3(lim);  u4 = semnal_u4(lim);  h = semnal_h(lim);    figure;  plot(t1,u1);  title('Grafic initial pentru semnalul u1');  xlabel("Secunde");  ylabel("u1");  legend("Semnalul u1");  pause(5);    ts_continous_conv(u1, h, t_u1);  title("Semnalul u1 in urma convolutiei");  xlabel("Secunde");  ylabel("y1(m/s)");  legend("Semnalul de iesire y1");  pause(5);    figure;  plot(t1,u2);  title('Grafic initial pentru semnalul u2');  xlabel("Secunde");  ylabel("u2");  legend("Semnalul u2");  pause(5);    ts_continous_conv(u2, h, t);  title('Semnalul u2 in urma convolutiei');  xlabel("Secunde");  ylabel("y2(m/s)");  legend("Semnalul de iesire y2");  pause(5);  %In urma analizei graficului masina ajunge la viteza ei maxima in 120 de secunde  %iar in cazul in care acceleratia este apasata la 50% ea ajunge in aproximativ  %20 de secunde    figure;  plot(t1,u3);  title('Grafic initial pentru semnalul u1');  xlabel("Secunde");  ylabel("u3");  legend("Semnalul u3");  pause(5);    ts_continous_conv(u3, h, t);  title('Semnalul u3 in urma convolutiei');  xlabel("Secunde");  ylabel("y3");  legend("Semnalul de iesire y3");  pause(5);    %Caracterul de sistem liniar este observat prin faptul ca semnalul de iesire  %y3 este unul sinusoidal, dar are o forma asemanatoare cu semnalul de iesire y2  %deoarece (u1+u2)(t) = u1(t) + u2(t);    figure;  plot(t1,u4);  title('Grafic initial pentru semnalul u4');  xlabel("Secunde");  ylabel("u4");  legend("Semnalul u4");  pause(5);    ts_continous_conv(u4, h, t);  title('Semnalul u4 in urma convolutiei');  xlabel("Secunde");  ylabel("y4");  legend("Semnalul de iesire y4");  pause(5);    %Se observa din graficele semnalelor de intrare u2 si u4, dar si din graficele   %semnalelor de iesire y2 si y4 propritatea de sistem invariant in timp, astfel   %incat daca semnalul de intrare are o intarziere la fel si semnalul de iesire   %va avea o intarziere.